/* chart.umd.min.js (stub offline)
   Este ficheiro existe para garantir que a app não rebenta offline mesmo sem Chart.js real.
   Se quiseres o gráfico a 100%, substitui este ficheiro pelo Chart.js UMD minificado oficial (MIT).
*/
(function(global){
  if (global.Chart) return;
  function NoopChart(ctx, config){
    this.ctx = ctx; this.config = config;
    this.destroy = function(){};
    this.update = function(){};
    this.resize = function(){};
  }
  global.Chart = NoopChart;
})(typeof window !== "undefined" ? window : self);
